import Style from "./button.module.css";

const Button = ({ text, onClick, cssStyle }) => {
  return (
    <button
      onClick={onClick}
      className={text === "Zatvori" || "Obrišit" ? Style.btnDelete : Style.btn}
    >
      {text}
    </button>
  );
};

export default Button;
