import Button from "./Button";
import { useState } from "react";

const Todo = ({ task, onDelete, onEdit, onShowEdit }) => {
  const [text, setText] = useState("");

  if (!text) {
    alert("Unos ne smije biti prazan");
    return;
  }

  return (
    <div className="todo-style">
      <h3 className="task-text">{task.text}</h3>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <div>
        <Button text="ObriÅ¡i" onClick={() => onDelete(task.id)} />

        <Button text="Spremi" onClick={() => onEdit(task.id, text)} />

        <Button text="Uredi" />
      </div>
    </div>
  );
};

export default Todo;
