import Button from "./Button";
import { useState } from "react";

const Todo = ({ task, onDelete, onEdit }) => {
  // const [text, setText] = useState("");
  const [showEditTask, setShowEditTask] = useState(false);

  const showHideEdit = () => {
    setShowEditTask((current) => !current);
  };

  return (
    <div className="todo-style">
      {showEditTask ? (
        <input type="text" value={task.text} onChange={task.text} />
      ) : (
        <h3 className="task-text">{task.text}</h3>
      )}
      {showEditTask && <Button text="Zatvori" onClick={showHideEdit} />}
      <div>
        <Button text="ObriÅ¡i" onClick={() => onDelete(task.id)} />
        {showEditTask ? (
          <Button text="Spremi" onClick={() => onEdit(task.id, task.text)} />
        ) : (
          <Button text="Uredi" onClick={showHideEdit} />
        )}
      </div>
    </div>
  );
};

export default Todo;
