import Button from "./Button";
import { useState } from "react";

const Todo = ({ task, onDelete, onEdit }) => {
  const [text, setText] = useState(task.text);
  const [showEditTask, setShowEditTask] = useState(false);

  const showHideEdit = () => {
    setShowEditTask((current) => !current);
  };

  return (
    <div className="todo-style">
      {showEditTask ? (
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      ) : (
        <h3 className="task-text">{task.text}</h3>
      )}
      {showEditTask && <Button text="Zatvori" onClick={showHideEdit} />}
      <div>
        <Button
          text="ObriÅ¡i"
          cssStyle="delete-btn btn"
          onClick={() => onDelete(task.id)}
        />
        {showEditTask ? (
          <Button text="Spremi" onClick={() => onEdit(task.id, text)} />
        ) : (
          <Button text="Uredi" onClick={showHideEdit} />
        )}
      </div>
    </div>
  );
};

export default Todo;
