import { useState } from "react";

const AddTodo = ({ onAdd }) => {
  const [title, setText] = useState("");
  const [category_id, setCategory] = useState(null);

  const onSubmit = (e) => {
    e.preventDefault();

    if (!title) {
      alert("Unos nesmije biti prazan!");
      return;
    }

    onAdd({ title, category_id });
    setText("");
    setCategory(null);
  };

  return (
    <div className="input-container">
      <form onSubmit={onSubmit} className="form-bt">
        <input
          type="text"
          placeholder="Unesi naziv ( npr. Kupi kruh )"
          value={title}
          onChange={(e) => setText(e.target.value)}
          className="form-input"
        ></input>
        <select
          className="dropdown-style"
          onChange={(e) => setCategory(e.target.id)}
        >
          <option id="1" value="1">
            Posao
          </option>
          <option id="2" value="2">
            Zdravlje
          </option>
          <option id="3" value="3">
            Zabava
          </option>
          <option id="4" value="4">
            Sport
          </option>
          <option id="5" value="5">
            Ostalo
          </option>
        </select>
        <input type="submit" value="Dodaj" className="btn"></input>
      </form>
    </div>
  );
};

export default AddTodo;
