import Button from "./Button";
import { useState } from "react";
import Style from "./todo.module.css";
import DeleteIcon from "@mui/icons-material/Delete";

const Todo = ({ task, onDelete, onEdit }) => {
  const [title, setText] = useState(task.title);
  const [showEditTask, setShowEditTask] = useState(false);

  const showHideEdit = () => {
    setShowEditTask((current) => !current);
  };

  return (
    <div className={Style.todoStyle}>
      {showEditTask ? (
        <textarea
          type="textarea"
          className={`${(Style.taskText, Style.txtArea)}`}
          value={title}
          onChange={(e) => setText(e.target.value)}
        />
      ) : (
        <div className={Style.taskContainer}>
          <span className={Style.taskText}>{task.title}</span>
          <div>
            <span className={Style.categoryText}>{task.category_title}</span>
          </div>
        </div>
      )}

      <div className={Style.buttons}>
        {showEditTask ? (
          <>
            <DeleteIcon onClick={showHideEdit} />
            {/*  <Button text="Zatvori" onClick={showHideEdit} /> */}
            <Button
              text="Spremi"
              onClick={() => {
                onEdit(task.id, title);
                setShowEditTask((current) => !current);
              }}
            />
          </>
        ) : (
          <>
            <DeleteIcon />
            <Button text="ObriÅ¡i" onClick={() => onDelete(task.id)} />
            <Button text="Uredi" onClick={showHideEdit} />
          </>
        )}
      </div>
    </div>
  );
};

export default Todo;
