import Button from "./Button";
import { useState } from "react";

const Todo = ({ task, onDelete, onEdit }) => {
  const [text, setText] = useState("");
  const [showEditTask, setShowEditTask] = useState(false);

  const showHide = () => {
    if (setShowEditTask) {
      setShowEditTask = false;
      return;
    }
    if (!setShowEditTask) {
      setShowEditTask = true;
      return;
    }
  };

  return (
    <div className="todo-style">
      <h3 className="task-text">{task.text}</h3>
      {showEditTask ? (
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      ) : (
        ""
      )}
      <div>
        <Button text="ObriÅ¡i" onClick={() => onDelete(task.id)} />
        <Button text="Spremi" onClick={() => onEdit(task.id, text)} />
        <Button text="Uredi" onClick={!setShowEditTask} />
      </div>
    </div>
  );
};

export default Todo;
