import Button from "./Button";
import { useState } from "react";

const Todo = ({ task, onDelete, onEdit }) => {
  const [text, setText] = useState(task.text);
  const [showEditTask, setShowEditTask] = useState(false);

  const showHideEdit = () => {
    setShowEditTask((current) => !current);
  };

  function auto_grow(element) {
    element.style.height = "5px";
    element.style.height = element.scrollHeight + "px";
  }

  return (
    <div className="todo-style">
      {showEditTask ? (
        <textarea
          type="textarea"
          className="edit-input task-text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      ) : (
        <div className="task-container">
          <span className="task-text">{task.text}</span>
        </div>
      )}
      {showEditTask && (
        <Button text="Zatvori" cssStyle="btn" onClick={showHideEdit} />
      )}
      <div>
        <Button
          text="ObriÅ¡i"
          cssStyle="delete-btn btn"
          onClick={() => onDelete(task.id)}
        />
        {showEditTask ? (
          <Button
            text="Spremi"
            cssStyle="btn"
            onClick={() => onEdit(task.id, text)}
          />
        ) : (
          <Button text="Uredi" cssStyle="btn" onClick={showHideEdit} />
        )}
      </div>
    </div>
  );
};

export default Todo;
